import React, { Component } from "react";
import axios from "axios";
import Alert from "./Alert";
import { login } from "../login";

class Login extends Component {
  state = { err: "" };

  login = (e) => {
    e.preventDefault();
    login(
      document.getElementById("email").value,
      document.getElementById("password").value
    ).then((r) => {
      if (r === true) {
        this.setState({ login: true });
      } else {
        this.setState({ err: r });
      }
    });
  };

  render() {
    return (
      <div className="w3-card-4" style={{ margin: "2rem" }}>
        <div className="w3-container w3-blue w3-center w3-xlarge">LOGIN</div>
        <div className="w3-container">
          {this.state.err.length > 0 && (
            <Alert
              message={`Check your form and try again! (${this.state.err})`}
            />
          )}
          <form onSubmit={this.login}>
            <p>
              <label htmlFor="email">Email</label>
              <input type="email" className="w3-input w3-border" id="email" />
            </p>
            <p>
              <label htmlFor="password">Password</label>
              <input
                type="password"
                className="w3-input w3-border"
                id="password"
              />
            </p>
            <p>
              <button type="submit" className="w3-button w3-blue">
                Login
              </button>
              {this.state.login && "You're logged in!"}
            </p>
          </form>
        </div>
      </div>
    );
  }
}

export default Login;
