import React, { Component } from "react";
import axios from "axios";
import Alert from "./Alert";

class Login extends Component {
  state = { err: "" };

  login = (e) => {
    e.preventDefault();
    axios
      .post("http://localhost:5000/api/login", {
        email: document.getElementById("email").value,
        pwd: document.getElementById("password").value,
      })
      .then((res) => {
        console.log(res.data);
        if (res.data.error) {
          this.setState({ err: res.data.error });
        } else {
          this.setState({ login: true });
        }
      });
  };

  render() {
    return (
      <div className="w3-card-4" style={{ margin: "2rem" }}>
        <div className="w3-container w3-blue w3-center w3-xlarge">LOGIN</div>
        <div className="w3-container">
          {this.state.err.length > 0 && (
            <Alert
              message={`Check your form and try again! (${this.state.err})`}
            />
          )}
          <form onSubmit={this.login}>
            <p>
              <label htmlFor="email">Email</label>
              <input type="email" class="w3-input w3-border" id="email" />
            </p>
            <p>
              <label htmlFor="password">Password</label>
              <input type="password" class="w3-input w3-border" id="password" />
            </p>
            <p>
              <button type="submit" class="w3-button w3-blue">
                Login
              </button>
              {this.state.register && <p>You're logged in!</p>}
            </p>
          </form>
        </div>
      </div>
    );
  }
}

export default Login;
